import React, { useState } from 'react';
import MaterialTable from 'material-table';
import DatePicker from "react-datepicker";
import TimePicker from 'react-time-picker';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';

import "react-datepicker/dist/react-datepicker.css";

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();
  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles(theme => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function MaterialTableDemo({ classes }) {
  const [startDate, setStartDate] = useState();
  const [startTime, setStartTime] = useState();
  const [endTime, setEndTime] = useState();
  const [open, setOpen] = useState();
  const [rowData, setRowData] = useState();
  const [state, setState] = useState({
    columns: [
      { title: 'Room Id', field: 'name' },
      { title: 'Building', field: 'surname' },
      { title: 'Room Name', field: 'birthYear', type: 'numeric' },
      { title: 'Birth Place', field: 'birthCity'},
    ],
    data: [
      { name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
      {
        name: 'Zerya BetÃ¼l',
        surname: 'Baran',
        birthYear: 2017,
        birthCity: 34,
      },
    ],
  });

  const [modalStyle] = React.useState(getModalStyle);

  const handleDateChange = date => {
    setStartDate(date)
  };

  const handleStartTimeChange = time => {
    setStartTime(time);
  }

  const handleEndTimeChange = time => {
    setEndTime(time);
  }

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <DatePicker
          selected={startDate}
          onChange={handleDateChange}
      />
      <TimePicker
        onChange={handleStartTimeChange}
        value={startTime}
      />
      <TimePicker
        onChange={handleEndTimeChange}
        value={endTime}
      />
      <MaterialTable style={ '2rem' }
        title="RSO Room Reserve"
        columns={state.columns}
        data={state.data}
        actions={[
          {
            icon: 'save',
            tooltip: 'Save User',
            onClick: (event, data) => {
              setOpen(true)
              setRowData(data)
              console.log(event + "\n")
              console.log(data);
            }
          }
        ]}

      />
      <Modal
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        open={open}
        onClose={handleClose}
      >
        <div style={modalStyle} className={classes.paper}>
          <h2 id="simple-modal-title">Text in a modal</h2>
          <p id="simple-modal-description">
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </p>
        </div>
      </Modal>
    </>
  );
}
